tauchen_process = function(grid, P, eps, mu, sigma) {
  time_span = length(eps)
  zt = rep(0, time_span)
  for(t in 2:time_span) {
    i = which(grid == zt[t-1])
    j = sum(cumsum(P[i, ]) < pnorm(eps[t], mu, sigma)) + 1
    zt[t] = grid[j]
  }
  return(zt)  
}
